<?php/** * Unit tests for blocks/calamardo/classes/calculator_average.php * * @license http://www.gnu.org/licenses/gpl-2.0.html GNU GPL v2 or later * @package block-evalcomix *//*if (!defined('MOODLE_INTERNAL')) {    die('Direct access to this script is forbidden.'); //  It must be included from a Moodle page}*/require_once(dirname(__FILE__) . '/../classes/calculator_average.php');require_once(dirname(__FILE__) . '/../../../config.php');//require_once($CFG->dirroot.'/grade/edit/tree/lib.php');//This require: global $CFG;class calculator_average_test extends UnitTestCase {	/**	* calculate  //$grades1, $grades2, $activities, $users	*/	public function test_calculate_ok() {	$array1 = array('2','4');	$array2 = array('5','6');	$arrayactivities = array('id'=>'5','name'=>'8');	$user1 = new stdClass();    $user1->id = 6;	$arrayusers[] = $user1;	$id = calculator_average::calculate($array1,$array2,$arrayactivities,$arrayusers);	$this->assertNotNull($id);	$this->assertIsA($id,'array');	unset($array1, $array2, $arrayactivities, $arrayusers, $user1, $id);	}	/**	* calculate_one_array  //$grades	*/	public function test_calculate_one_array_ok() {	$arraygrades = array('5','6');	$id = calculator_average::calculate_one_array($arraygrades);	$this->assertNotNull($id);	$this->assertIsA($id,'float');	unset($arraygrades, $id);	}}